import { Request, Response } from 'express';
import { storage } from '../models/storage';

export class AdminController {
  static async getAllUsers(req: Request, res: Response) {
    try {
      const users = await storage.getAllUsers();
      res.json(users);
    } catch (error) {
      console.error("Error fetching users:", error);
      res.status(500).json({ message: "Failed to fetch users" });
    }
  }

  static async toggleUserStatus(req: Request, res: Response) {
    try {
      const { userId } = req.params;
      const user = await storage.toggleUserStatus(parseInt(userId));
      res.json(user);
    } catch (error) {
      console.error("Error toggling user status:", error);
      res.status(500).json({ message: "Failed to toggle user status" });
    }
  }

  static async updateUser(req: Request, res: Response) {
    try {
      const { userId } = req.params;
      const userData = req.body;
      const user = await storage.updateUser(parseInt(userId), userData);
      res.json(user);
    } catch (error) {
      console.error("Error updating user:", error);
      res.status(500).json({ message: "Failed to update user" });
    }
  }

  static async updateUserWallet(req: Request, res: Response) {
    try {
      const { userId } = req.params;
      const { amount, reason } = req.body;
      const result = await storage.updateUserWallet(parseInt(userId), amount, reason);
      res.json(result);
    } catch (error) {
      console.error("Error updating wallet:", error);
      res.status(500).json({ message: "Failed to update wallet" });
    }
  }

