import { Router } from 'express';
import { AdminController } from '../controllers/adminController';
import { requireAuth, requireAdmin } from '../middleware/auth';

const router = Router();

// Require authentication for all admin routes
router.use(requireAuth);

// Lucky numbers (accessible to all authenticated users)
router.get('/lucky-numbers', AdminController.getLuckyNumbers);

// Guessing posts (accessible to all authenticated users)
router.get('/guessing-posts', AdminController.getGuessingPosts);
router.post('/guessing-posts', AdminController.createGuessingPost);

// Admin only routes
router.use(requireAdmin);
router.get('/users', AdminController.getAllUsers);
router.put('/users/:userId/toggle-status', AdminController.toggleUserStatus);
router.put('/users/:userId', AdminController.updateUser);
router.post('/users/:userId/wallet', AdminController.updateUserWallet);

router.post('/lucky-numbers', AdminController.addLuckyNumber);
router.put('/lucky-numbers/:id', AdminController.updateLuckyNumber);
router.delete('/lucky-numbers/:id', AdminController.deleteLuckyNumber);

router.get('/app-settings', AdminController.getAppSettings);
router.put('/app-settings/:settingKey', AdminController.updateAppSetting);

export { router as adminRoutes };

