                      </div>
                      {sidebarExpanded && (
                        isExpanded ? <ChevronDown className="w-4 h-4" /> : <ChevronRight className="w-4 h-4" />
                      )}
                    </button>

                    {/* Children items */}
                    {isExpanded && sidebarExpanded && (
                      <div className="ml-6 mt-1 space-y-1">
                        {item.children.map((child) => {
                          const ChildIconComponent = child.icon;
