import { useState, useEffect } from "react";
import { ArrowLeft, Wallet, Target, Palette, Plane, Dice1, Zap, CreditCard, TrendingUp, DollarSign, Play, RotateCcw, Trophy, Star } from "lucide-react";
import { useAuth } from "@/hooks/useAuth";
import { useQuery } from "@tanstack/react-query";
import { useLocation } from "wouter";
import { Button } from "@/components/ui/button";
import { Card, CardContent } from "@/components/ui/card";

interface GameCard {
  id: string;
  title: string;
  description: string;
  icon: any;
  bgGradient: string;
  hoverGradient: string;
}

export default function PlayGames() {
  const { user } = useAuth();
  const [, setLocation] = useLocation();

  // Fetch updated user data to ensure wallet balance is current
  const { data: allUsers } = useQuery({
    queryKey: ["/api/admin/users"],
    refetchInterval: 30000, // Refresh every 30 seconds
    staleTime: 10000,
  });

  // Get current user with latest wallet balance
  const currentUser = allUsers?.find((u: any) => u.id === user?.id || u.email === user?.email);

  const [selectedGame, setSelectedGame] = useState<string | null>(null);
  const [selectedMatkaType, setSelectedMatkaType] = useState<string | null>(null);
  const [selectedCoin, setSelectedCoin] = useState<number>(10);
  const [customCoin, setCustomCoin] = useState<string>("");
  const [selectedNumber, setSelectedNumber] = useState<number | null>(null);
  const [selectedColors, setSelectedColors] = useState<string[]>([]);

  // Color King game states
  const [isColorKingPlaying, setIsColorKingPlaying] = useState(false);
  const [colorKingResult, setColorKingResult] = useState<string | null>(null);
  const [colorKingHistory, setColorKingHistory] = useState<{color: string, result: 'win' | 'lose', amount: number, timestamp: number}[]>([]);
  const [colorKingTimer, setColorKingTimer] = useState(0);
  const [nextRoundTimer, setNextRoundTimer] = useState(0);
  const [currentRound, setCurrentRound] = useState(1);
  const [totalWins, setTotalWins] = useState(0);
  const [totalLoses, setTotalLoses] = useState(0);

  // Color King timer effect
  useEffect(() => {
    let interval: NodeJS.Timeout;
    if (isColorKingPlaying && colorKingTimer > 0) {
      interval = setInterval(() => {
        setColorKingTimer(prev => prev - 1);
      }, 1000);
    } else if (isColorKingPlaying && colorKingTimer === 0 && !colorKingResult) {
      // Generate result when timer reaches 0
      generateColorKingResult();
    }
    return () => clearInterval(interval);
  }, [isColorKingPlaying, colorKingTimer, colorKingResult]);

  // Next round timer effect
  useEffect(() => {
    let interval: NodeJS.Timeout;
    if (nextRoundTimer > 0) {
      interval = setInterval(() => {
        setNextRoundTimer(prev => prev - 1);
      }, 1000);
    } else if (nextRoundTimer === 0 && colorKingResult) {
      // Start next round
      resetColorKingRound();
    }
    return () => clearInterval(interval);
  }, [nextRoundTimer, colorKingResult]);

  const generateColorKingResult = () => {
    const colors = ['red', 'green', 'yellow', 'blue'];
    const winningColor = colors[Math.floor(Math.random() * colors.length)];
    setColorKingResult(winningColor);

    // Check if user won
    if (selectedColors.includes(winningColor)) {
      const winAmount = selectedCoin * selectedColors.length * 3.5;
      setTotalWins(prev => prev + 1);
      setColorKingHistory(prev => [...prev, {
        color: winningColor,
        result: 'win',
        amount: winAmount,
        timestamp: Date.now()
      }]);
    } else {
      setTotalLoses(prev => prev + 1);
      setColorKingHistory(prev => [...prev, {
        color: winningColor,
        result: 'lose',
        amount: selectedCoin,
        timestamp: Date.now()
      }]);
    }

    // Start next round timer
    setNextRoundTimer(10);
  };

  const resetColorKingRound = () => {
    setColorKingResult(null);
    setSelectedColors([]);
    setCurrentRound(prev => prev + 1);
    setNextRoundTimer(0);
    setColorKingTimer(0);
    setIsColorKingPlaying(false);
  };

  const startColorKingRound = () => {
    if (selectedColors.length === 0 || !selectedCoin) return;
    setIsColorKingPlaying(true);
    setColorKingTimer(15); // 15 second countdown
  };

