    // Smart back navigation - go back step by step
    if (selectedGame === "matka" && selectedMatkaType) {
      // From betting interface to matka options
      handleBackToMatkaOptions();
    } else if (selectedGame === "matka" && !selectedMatkaType) {
      // From matka options to game selection
      handleBackToGames();
    } else if (selectedGame && selectedGame !== "matka") {
      // From any other game to game selection
      handleBackToGames();
    } else {
      // From game selection to dashboard
      setLocation("/");
    }
  };

  const handleBackToGames = () => {
    // Reset matka states and go back to games
    setSelectedGame(null);
    setSelectedMatkaType(null);
    setSelectedCoin(10);
    setCustomCoin("");
    setSelectedNumber(null);
    setSelectedColors([]);
  };

