    { id: "yellow", name: "Yellow", color: "bg-yellow-500", emoji: "ðŸŸ¡" },
    { id: "blue", name: "Blue", color: "bg-blue-500", emoji: "ðŸ”µ" }
  ];

  return (
    <div className="min-h-screen bg-gradient-to-br from-white via-blue-50/20 to-purple-50/40 relative">
      {/* Floating Orbs */}
      <div className="floating-orb floating-orb-1"></div>
