                    <Trophy className="h-5 w-5" />
                    Game Stats
                  </h3>
                  <div className="space-y-2">
                    <div className="flex justify-between">
                      <span className="text-green-700">Total Wins:</span>
                      <span className="font-bold text-green-800">{totalWins}</span>
                    </div>
                    <div className="flex justify-between">
                      <span className="text-red-700">Total Losses:</span>
                      <span className="font-bold text-red-800">{totalLoses}</span>
                    </div>
                    <div className="flex justify-between">
                      <span className="text-blue-700">Win Rate:</span>
                      <span className="font-bold text-blue-800">
                        {totalWins + totalLoses > 0 ? ((totalWins / (totalWins + totalLoses)) * 100).toFixed(1) : 0}%
                      </span>
                    </div>
                  </div>
                </div>

                {/* Recent Results */}
                <div className="glass-card p-4">
                  <h3 className="text-lg font-bold text-gray-800 mb-3">Recent Results</h3>
                  <div className="space-y-2 max-h-40 overflow-y-auto">
                    {colorKingHistory.slice(-5).reverse().map((entry, index) => (
                      <div key={index} className={`flex items-center justify-between p-2 rounded-lg ${
                        entry.result === 'win' ? 'bg-green-100' : 'bg-red-100'
                      }`}>
                        <div className="flex items-center gap-2">
                          <span className="text-2xl">
                            {colorOptions.find(c => c.id === entry.color)?.emoji}
                          </span>
                          <span className="font-semibold capitalize">{entry.color}</span>
                        </div>
                        <div className={`font-bold ${entry.result === 'win' ? 'text-green-600' : 'text-red-600'}`}>
                          {entry.result === 'win' ? '+' : '-'}â‚¹{entry.amount}
                        </div>
                      </div>
                    ))}
                    {colorKingHistory.length === 0 && (
                      <p className="text-gray-500 text-sm">No games played yet</p>
                    )}
                  </div>
                </div>
              </div>

              {/* Center Column - Game Area */}
              <div className="space-y-6">
                {/* Game Timer */}
                {isColorKingPlaying && (
                  <div className="glass-accent p-6 text-center rounded-3xl">
                    <h3 className="text-xl font-bold text-pink-800 mb-4">Game in Progress</h3>
                    <div className="text-6xl font-bold text-pink-600 mb-2 animate-pulse">
                      {colorKingTimer}
                    </div>
                    <p className="text-pink-700">Seconds remaining</p>
                  </div>
                )}

                {/* Result Display */}
                {colorKingResult && (
                  <div className="glass-card p-6 text-center rounded-3xl">
                    <h3 className="text-2xl font-bold text-yellow-800 mb-4">ðŸŽ‰ Result!</h3>
                    <div className="flex justify-center items-center gap-4 mb-4">
                      <span className="text-6xl animate-bounce">
                        {colorOptions.find(c => c.id === colorKingResult)?.emoji}
                      </span>
                      <div>
                        <div className="text-3xl font-bold capitalize text-yellow-800">
                          {colorKingResult}
                        </div>
                        <div className={`text-xl font-semibold ${
                          selectedColors.includes(colorKingResult) ? 'text-green-600' : 'text-red-600'
                        }`}>
                          {selectedColors.includes(colorKingResult) ? 'ðŸŽ‰ You Won!' : 'ðŸ˜” You Lost'}
                        </div>
                      </div>
                    </div>
                    <p className="text-yellow-700">
                      Next round starts in {nextRoundTimer} seconds
                    </p>
                  </div>
                )}

                {/* Color Selection */}
                <div className="glass-card p-6 rounded-3xl">
                  <h3 className="text-xl font-bold mb-4 text-purple-800 flex items-center gap-2">
                    <Palette className="h-6 w-6" />
                    Select Colors (4 Color Luck Game)
                  </h3>
                  <div className="grid grid-cols-2 gap-4">
                    {colorOptions.map((color) => (
                      <Button
                        key={color.id}
                        variant={selectedColors.includes(color.id) ? "default" : "outline"}
                        className={`h-20 text-white font-bold text-lg ${color.color} hover:opacity-80 transition-all duration-300 transform hover:scale-105 rounded-3xl ${
                          selectedColors.includes(color.id) ? 'scale-105 shadow-xl ring-4 ring-white' : ''
                        } ${isColorKingPlaying ? 'opacity-50 cursor-not-allowed' : ''}`}
                        disabled={isColorKingPlaying}
                        onClick={() => {
                          if (selectedColors.includes(color.id)) {
                            setSelectedColors(selectedColors.filter(c => c !== color.id));
                          } else {
                            setSelectedColors([...selectedColors, color.id]);
                          }
                        }}
                      >
                        <div className="flex items-center gap-2">
                          <span className="text-2xl">{color.emoji}</span>
                          {color.name}
                        </div>
                        {selectedColors.includes(color.id) && (
                          <div className="absolute -top-2 -right-2 w-6 h-6 bg-white rounded-full flex items-center justify-center">
                            <span className="text-green-600 font-bold">âœ“</span>
                          </div>
                        )}
                      </Button>
                    ))}
                  </div>
                </div>
              </div>

              {/* Right Column - Bet Controls */}
              <div className="space-y-4">
                {/* Bet Amount */}
                <div className="glass-card p-4 rounded-3xl">
                  <h3 className="text-lg font-bold mb-4 text-yellow-800 flex items-center gap-2">
                    <DollarSign className="h-5 w-5" />
                    Select Bet Amount
                  </h3>
                  <div className="grid grid-cols-2 gap-2">
                    {coinOptions.map((coin) => (
                      <Button
                        key={coin}
                        variant={selectedCoin === coin ? "default" : "outline"}
                        className={`aspect-square text-sm font-bold transition-all duration-300 rounded-full ${
                          selectedCoin === coin 
                            ? "bg-gradient-to-r from-yellow-500 to-orange-500 text-white shadow-lg scale-105" 
                            : "hover:bg-yellow-100 hover:border-yellow-300 hover:scale-105"
                        } ${isColorKingPlaying ? 'opacity-50 cursor-not-allowed' : ''}`}
                        disabled={isColorKingPlaying}
                        onClick={() => setSelectedCoin(coin)}
                      >
                        â‚¹{coin}
                      </Button>
                    ))}
                  </div>
                </div>

                {/* Current Bet Info */}
                <div className="glass-accent p-4 rounded-3xl">
                  <h3 className="text-lg font-bold text-pink-800 mb-3">Current Bet</h3>
                  <div className="space-y-2">
                    <div className="flex justify-between">
                      <span className="text-pink-700">Colors:</span>
                      <span className="font-bold text-pink-800">
                        {selectedColors.length > 0 ? selectedColors.join(", ") : "None"}
                      </span>
                    </div>
                    <div className="flex justify-between">
                      <span className="text-pink-700">Bet:</span>
                      <span className="font-bold text-pink-800">â‚¹{selectedCoin}</span>
                    </div>
                    <div className="flex justify-between">
                      <span className="text-pink-700">Potential Win:</span>
                      <span className="font-bold text-green-600">
                        â‚¹{(selectedCoin * selectedColors.length * 3.5).toFixed(2)}
                      </span>
                    </div>
                  </div>
                </div>

                {/* Game Controls */}
                <div className="space-y-3">
                  <Button 
                    size="lg"
                    className="w-full bg-gradient-to-r from-purple-500 to-pink-500 hover:from-purple-600 hover:to-pink-600 text-white font-bold py-4 shadow-lg hover:shadow-xl transition-all duration-300 transform hover:scale-105 rounded-full"
                    disabled={selectedColors.length === 0 || !selectedCoin || isColorKingPlaying}
                    onClick={startColorKingRound}
                  >
                    <Play className="h-5 w-5 mr-2" />
                    {isColorKingPlaying ? 'Game Running...' : 'Start Round'}
                  </Button>

                  <Button 
                    variant="outline" 
                    size="lg"
                    className="w-full glass-card hover:glass-primary transition-all duration-300 rounded-full"
                    onClick={() => {
                      setSelectedColors([]);
                      setSelectedCoin(10);
                    }}
                    disabled={isColorKingPlaying}
                  >
                    <RotateCcw className="h-4 w-4 mr-2" />
                    Reset Selections
                  </Button>

                  <Button 
                    variant="outline" 
                    size="lg"
                    onClick={handleBackToGames}
                    className="w-full glass-card hover:glass-primary transition-all duration-300 rounded-full"
                  >
                    â† Back to Games
                  </Button>
                </div>
              </div>
            </div>
          </div>
        ) : (
          // Other Games Coming Soon
          <div className="max-w-2xl mx-auto text-center">
            <div className="glass-card p-8 text-center">
              <div className="text-6xl mb-4">ðŸš§</div>
              <h2 className="text-2xl font-bold text-gray-900 mb-4">
                {gameCards.find(g => g.id === selectedGame)?.title}
              </h2>
              <p className="text-gray-600 mb-6">
                This game is currently under development and will be available soon!
              </p>
              <Button 
                onClick={handleBackToGames}
                className="bg-gradient-to-r from-blue-500 to-blue-600 hover:from-blue-600 hover:to-blue-700 text-white rounded-full"
              >
                â† Back to Games
              </Button>
            </div>
          </div>
        )}
      </main>
    </div>
  );
}

client/src/pages/Settings.tsx.content:
import { useState } from "react";
import { ArrowLeft, User, Bell, Shield, Eye, EyeOff, Save, Wallet } from "lucide-react";
import { useAuth } from "@/hooks/useAuth";
import { useQuery } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Switch } from "@/components/ui/switch";

export default function Settings() {
  const { user } = useAuth();

  // Fetch updated user data for wallet balance
  const { data: allUsers } = useQuery({
    queryKey: ["/api/admin/users"],
    refetchInterval: 30000, // Refresh every 30 seconds
    staleTime: 10000,
  });

  // Get current user with latest wallet balance
  const currentUser = allUsers?.find((u: any) => u.id === user?.id || u.email === user?.email);

  const [showPassword, setShowPassword] = useState(false);
  const [passwordData, setPasswordData] = useState({
    newPassword: "",
    confirmPassword: ""
  });
  const [notifications, setNotifications] = useState({
    gameResults: true,
    promotions: false,
    winnings: true,
    maintenance: true
  });

  const handleBackToDashboard = () => {
    window.history.back();
  };

  const handlePasswordChange = (field: string, value: string) => {
    setPasswordData(prev => ({
      ...prev,
      [field]: value
    }));
  };

  const handleNotificationChange = (field: string, value: boolean) => {
    setNotifications(prev => ({
      ...prev,
      [field]: value
    }));
  };

  const handleUpdatePassword = () => {
    // Here you would typically make an API call to update password
    console.log("Updating password");
    setPasswordData({ newPassword: "", confirmPassword: "" });
  };

  const handleSaveNotifications = () => {
    // Here you would typically make an API call to update notification settings
    console.log("Saving notifications:", notifications);
  };

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Header */}
      <header className="bg-white shadow-sm border-b sticky top-0 z-50">
        <div className="px-4 py-3 flex items-center justify-between">
          <button 
            onClick={handleBackToDashboard}
            className="p-2 hover:bg-gray-100 rounded-lg flex items-center gap-2"
          >
            <ArrowLeft className="h-5 w-5" />
            <span className="text-sm font-medium">Back</span>
          </button>

          <h1 className="text-lg font-semibold text-gray-900">Settings</h1>

          <div className="flex items-center gap-4">
            {/* Wallet */}
            <div className="flex items-center gap-2 bg-green-50 px-3 py-2 rounded-lg">
              <Wallet className="h-4 w-4 text-green-600" />
              <span className="text-green-700 font-semibold">
                â‚¹{currentUser?.wallet_balance || '0.00'}
              </span>
            </div>

            {/* Profile */}
            <div className="w-8 h-8 bg-blue-500 rounded-full flex items-center justify-center text-white font-semibold">
              {user?.name?.charAt(0)?.toUpperCase() || "U"}
            </div>
          </div>
        </div>
      </header>

      {/* Main Content */}
      <main className="p-4 md:p-6">
        <div className="max-w-2xl mx-auto space-y-6">

          {/* Profile Information */}
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <User className="h-5 w-5" />
                Profile Information
              </CardTitle>
              <p className="text-sm text-gray-600">Your account details (Read-only)</p>
            </CardHeader>
            <CardContent className="space-y-4">
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <div>
                  <Label htmlFor="name">Full Name</Label>
                  <Input
                    id="name"
                    value={user?.name || ""}
                    disabled
                    className="mt-1 bg-gray-50"
                  />
                </div>
                <div>
                  <Label htmlFor="email">Email</Label>
                  <Input
                    id="email"
                    type="email"
                    value={user?.email || ""}
                    disabled
                    className="mt-1 bg-gray-50"
                  />
                </div>
              </div>

              <div>
                <Label htmlFor="mobile">Mobile Number</Label>
                <Input
                  id="mobile"
                  value={user?.mobile || ""}
                  disabled
                  className="mt-1 bg-gray-50"
                />
              </div>

              <div>
                <Label htmlFor="userId">User ID</Label>
                <Input
                  id="userId"
                  value={currentUser?.unique_user_id || "Loading..."}
                  disabled
                  className="mt-1 bg-gray-50"
                />
              </div>

              <div>
                <Label htmlFor="walletBalance">Wallet Balance</Label>
                <Input
                  id="walletBalance"
                  value={`â‚¹${currentUser?.wallet_balance || 0}`}
                  disabled
                  className="mt-1 bg-gray-50"
                />
              </div>
            </CardContent>
          </Card>

          {/* Change Password */}
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <Shield className="h-5 w-5" />
                Change Password
              </CardTitle>
              <p className="text-sm text-gray-600">Update your password for security</p>
            </CardHeader>
            <CardContent className="space-y-4">
              <div>
                <Label htmlFor="newPassword">New Password</Label>
                <div className="relative">
                  <Input
                    id="newPassword"
                    type={showPassword ? "text" : "password"}
                    value={passwordData.newPassword}
                    onChange={(e) => handlePasswordChange("newPassword", e.target.value)}
                    className="mt-1 pr-10"
                    placeholder="Enter new password"
                  />
                  <button
                    type="button"
                    onClick={() => setShowPassword(!showPassword)}
                    className="absolute right-3 top-1/2 transform -translate-y-1/2 text-gray-500"
                  >
                    {showPassword ? <EyeOff className="h-4 w-4" /> : <Eye className="h-4 w-4" />}
                  </button>
                </div>
              </div>

              <div>
                <Label htmlFor="confirmPassword">Confirm Password</Label>
                <Input
                  id="confirmPassword"
                  type={showPassword ? "text" : "password"}
                  value={passwordData.confirmPassword}
                  onChange={(e) => handlePasswordChange("confirmPassword", e.target.value)}
                  className="mt-1"
                  placeholder="Confirm new password"
                />
              </div>

              <Button 
                onClick={handleUpdatePassword}
                className="w-full" 
                disabled={!passwordData.newPassword || passwordData.newPassword !== passwordData.confirmPassword}
              >
                <Save className="h-4 w-4 mr-2" />
                Update Password
              </Button>
            </CardContent>
          </Card>

          {/* Notification Settings */}
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <Bell className="h-5 w-5" />
                Notification Settings
              </CardTitle>
              <p className="text-sm text-gray-600">Choose what notifications you want to receive</p>
            </CardHeader>
            <CardContent className="space-y-4">
              <div className="flex items-center justify-between">
                <div>
                  <Label htmlFor="gameResults">Game Results</Label>
                  <p className="text-sm text-gray-600">Get notified when game results are announced</p>
                </div>
                <Switch
                  id="gameResults"
                  checked={notifications.gameResults}
                  onCheckedChange={(value) => handleNotificationChange("gameResults", value)}
                />
              </div>

              <div className="flex items-center justify-between">
                <div>
                  <Label htmlFor="promotions">Promotions & Offers</Label>
                  <p className="text-sm text-gray-600">Receive special offers and promotions</p>
                </div>
                <Switch
                  id="promotions"
                  checked={notifications.promotions}
                  onCheckedChange={(value) => handleNotificationChange("promotions", value)}
                />
              </div>

              <div className="flex items-center justify-between">
                <div>
                  <Label htmlFor="winnings">Winnings & Payouts</Label>
                  <p className="text-sm text-gray-600">Get notified about your winnings</p>
                </div>
                <Switch
                  id="winnings"
                  checked={notifications.winnings}
                  onCheckedChange={(value) => handleNotificationChange("winnings", value)}
                />
              </div>

              <div className="flex items-center justify-between">
                <div>
                  <Label htmlFor="maintenance">Maintenance Updates</Label>
                  <p className="text-sm text-gray-600">Important system maintenance notifications</p>
                </div>
                <Switch
                  id="maintenance"
                  checked={notifications.maintenance}
                  onCheckedChange={(value) => handleNotificationChange("maintenance", value)}
                />
              </div>

              <Button onClick={handleSaveNotifications} className="w-full">
                <Save className="h-4 w-4 mr-2" />
                Save Notification Settings
              </Button>
            </CardContent>
          </Card>

          {/* Account Information */}
          <Card>
            <CardHeader>
              <CardTitle className="text-blue-600">Account Information</CardTitle>
              <p className="text-sm text-gray-600">Important account details</p>
            </CardHeader>
            <CardContent className="space-y-3">
              <div className="bg-yellow-50 border border-yellow-200 rounded-lg p-3">
                <p className="text-sm text-yellow-800">
                  <strong>Account Status:</strong> Active
                </p>
              </div>
              <div className="bg-blue-50 border border-blue-200 rounded-lg p-3">
                <p className="text-sm text-blue-800">
                  <strong>Member Since:</strong> {currentUser?.created_at ? new Date(currentUser.created_at).toLocaleDateString('hi-IN') : "Recently"}
                </p>
              </div>
            </CardContent>
          </Card>

        </div>
      </main>
    </div>
  );
}

client/src/pages/SimpleDashboard.tsx.content:
import { useState } from "react";
import { useLocation } from "wouter";
import { Menu, Home, Gamepad2, BarChart3, Phone, History, CreditCard, Gift, Settings, LogOut, Wallet, Play, Users, DollarSign } from "lucide-react";
import { useAuth } from "@/hooks/useAuth";
import { useQuery } from "@tanstack/react-query";
import LiveResultsSection from "@/components/LiveResultsSection";

export default function SimpleDashboard() {
  const { user, logout } = useAuth();
  const [isSidebarOpen, setIsSidebarOpen] = useState(false);
  const [, setLocation] = useLocation();

  // Fetch current user data to get latest wallet balance
  const { data: currentUser } = useQuery({
    queryKey: ["/api/admin/users"],
    enabled: !!user?.id,
    refetchInterval: 5000, // Refresh every 5 seconds
    staleTime: 0,
    select: (data) => {
      // Find current user from all users data
      return data.find((u: any) => u.id === user?.id || u.email === user?.email);
    },
  });

  // Fetch user transactions
  const { data: userTransactions } = useQuery({
    queryKey: ["/api/user/transactions", user?.id],
    enabled: !!user?.id,
    refetchInterval: 30000, // Refresh every 30 seconds
    staleTime: 10000,
  });

  const handleLogout = () => {
    logout();
    setIsSidebarOpen(false);
  };

  const handleNavigation = (path: string) => {
    window.scrollTo(0, 0);
    setLocation(path);
    setIsSidebarOpen(false);
  };

  const sidebarItems = [
    { icon: Home, label: "Home", href: "/" },
    { icon: Gamepad2, label: "Play Games", href: "/play-games" },
    { icon: BarChart3, label: "Game Results", href: "/game-results" },
    { icon: Phone, label: "Game Rates", href: "/game-rates" },
    { icon: History, label: "My Orders", href: "/my-orders" },
    { icon: CreditCard, label: "My Bid", href: "/my-bid" },
    { icon: Wallet, label: "My Wallet", href: "/my-wallet" },
    { icon: Gift, label: "How to Play", href: "/how-to-play" },
    { icon: Settings, label: "Settings", href: "/settings" },
  ];

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Header */}
      <header className="bg-white shadow-sm border-b sticky top-0 z-50">
        <div className="px-4 py-3 flex items-center justify-between">
          {/* Left Side - Hamburger Menu */}
          <button 
            onClick={() => setIsSidebarOpen(!isSidebarOpen)}
            className="p-2 hover:bg-gray-100 rounded-lg"
          >
            <Menu className="h-6 w-6" />
          </button>

          {/* Right Side - Profile & Wallet */}
          <div className="flex items-center gap-4">
            {/* Wallet */}
            <div className="flex items-center gap-2 bg-green-50 px-3 py-2 rounded-lg">
              <Wallet className="h-4 w-4 text-green-600" />
              <span className="text-green-700 font-semibold">
                â‚¹{currentUser?.wallet_balance || '0.00'}
              </span>
            </div>

            {/* Profile */}
            <div className="w-8 h-8 bg-blue-500 rounded-full flex items-center justify-center text-white font-semibold">
              {user?.name?.charAt(0)?.toUpperCase() || "U"}
            </div>
          </div>
        </div>
      </header>

      {/* Sidebar Overlay */}
      {isSidebarOpen && (
        <div 
          className="fixed inset-0 bg-black bg-opacity-50 z-40"
          onClick={() => setIsSidebarOpen(false)}
        />
      )}

      {/* Sidebar */}
      <div className={`fixed left-0 top-16 h-full bg-white shadow-lg border-r transition-transform duration-300 z-50 w-64 ${
        isSidebarOpen ? 'translate-x-0' : '-translate-x-full'
      }`}>
        <nav className="p-4 space-y-2">
          {sidebarItems.map((item) => (
            <button
              key={item.href}
              className="w-full flex items-center gap-3 text-gray-700 hover:text-gray-900 hover:bg-gray-100 p-3 rounded-lg text-left"
              onClick={() => handleNavigation(item.href)}
            >
              <item.icon className="h-5 w-5" />
              {item.label}
            </button>
          ))}
          <button
            className="w-full flex items-center gap-3 text-red-600 hover:text-red-700 hover:bg-red-50 p-3 rounded-lg text-left mt-4"
            onClick={handleLogout}
          >
            <LogOut className="h-5 w-5" />
            Logout
          </button>
        </nav>
      </div>

      {/* Main Content */}
      <main className="p-4 md:p-6">
        {/* Welcome Section */}
        <div className="bg-white rounded-lg shadow-sm p-6 mb-6">
          <div className="flex items-center gap-2 mb-2">
            <span className="text-2xl">ðŸ‘‹</span>
            <h1 className="text-2xl font-bold text-gray-900">
              Hey {user?.name?.toUpperCase() || "USER"}
            </h1>
          </div>
          <p className="text-gray-600 text-sm">
            Welcome to the World's Biggest Matka Game Website
          </p>
          <p className="text-gray-500 text-xs mt-1">
            User ID: {currentUser?.unique_user_id || user?.unique_user_id}
          </p>
        </div>

        {/* Play Games Section */}
        <div className="bg-white rounded-lg shadow-sm p-6 mb-6">
          <button 
            onClick={() => {
              window.scrollTo(0, 0);
              setLocation("/play-games");
            }}
            className="w-full bg-gradient-to-r from-blue-600 to-purple-600 hover:from-blue-700 hover:to-purple-700 text-white py-4 text-lg font-semibold rounded-lg mb-6"
          >
            <Play className="inline mr-2 h-5 w-5" />
            PLAY GAMES
          </button>

        </div>

