// API configuration for separated backend
const API_BASE_URL = import.meta.env.VITE_API_URL || 'http://localhost:3000';

export async function apiRequest(endpoint: string, options: RequestInit = {}) {
  const url = `${API_BASE_URL}${endpoint}`;

  const defaultOptions: RequestInit = {
    credentials: 'include',
    headers: {
      'Content-Type': 'application/json',
      ...options.headers,
    },
    ...options,
  };

  const response = await fetch(url, defaultOptions);

  if (!response.ok) {
    const error = await response.json().catch(() => ({ message: 'Network error' }));
    throw new Error(error.message || 'Request failed');
  }

  return response.json();
}

// Auth API
export const authAPI = {
  login: (credentials: any) => apiRequest('/api/auth/login', {
    method: 'POST',
    body: JSON.stringify(credentials),
  }),

  register: (userData: any) => apiRequest('/api/auth/register', {
    method: 'POST',
    body: JSON.stringify(userData),
  }),

  logout: () => apiRequest('/api/auth/logout', { method: 'POST' }),

  getCurrentUser: () => apiRequest('/api/auth/me'),
};

// Game API
export const gameAPI = {
  getLiveResults: () => apiRequest('/api/live-results'),
  getChartResults: (gameName: string) => apiRequest(`/api/charts/${gameName}`),
  getAllGames: () => apiRequest('/api/admin/games'),
  addGame: (gameData: any) => apiRequest('/api/admin/games', {
    method: 'POST',
    body: JSON.stringify(gameData),
  }),
  updateGameResult: (gameId: number, resultData: any) => apiRequest(`/api/admin/games/${gameId}/result`, {
    method: 'PUT',
    body: JSON.stringify(resultData),
  }),
};

// Admin API
export const adminAPI = {
  getAllUsers: () => apiRequest('/api/admin/users'),
  getLuckyNumbers: () => apiRequest('/api/admin/lucky-numbers'),
  getAppSettings: () => apiRequest('/api/admin/app-settings'),
  getGuessingPosts: () => apiRequest('/api/admin/guessing-posts'),
};

